import React, { useRef, useEffect } from "react";
import { View, Text, FlatList, Image, StyleSheet, Dimensions,Animated,TouchableOpacity } from "react-native";

const { width,height } = Dimensions.get("window");

const data = [
  { id: "1", image: "data:image/jpeg;base64,", title: "Item 1", price: "$30" },
  { id: "2", image: "data:image/jpeg;base64,", title: "Item 2", price: "$40" },
  { id: "3", image: "data:image/jpeg;base64,", title: "Item 3", price: "$20" },
  { id: "4", image: "data:image/jpeg;base64,", title: "Item 1", price: "$30" },
  { id: "5", image: "data:image/jpeg;base64,", title: "Item 2", price: "$40" },
  { id: "6", image: "data:image/jpeg;base64,", title: "Item 3", price: "$20" },

];

const ShoppingCarousel = () => {
  const flatListRef = useRef(null);
  const currentIndex = useRef(0);

  useEffect(() => {
    const interval = setInterval(() => {
      currentIndex.current = (currentIndex.current + 1) % data.length;
      flatListRef.current.scrollToIndex({ index: currentIndex.current, animated: true });
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.card}>
      <Image source={{ uri: item.image }} style={styles.image} />
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        ref={flatListRef}
        data={data}
        renderItem={renderItem}
        horizontal
        keyExtractor={(item) => item.id}
        showsHorizontalScrollIndicator={false}
        pagingEnabled
        contentContainerStyle={{ alignItems: "center" }}
      />
      <View style={styles.dotsContainer}>
        {data.map((_, index) => (
          <View
            key={index}
            style={[
              styles.dot,
              currentIndex.current === index && styles.activeDot,
            ]}
          />
        ))}
      </View>
      <TouchableOpacity style={styles.exploreButton}>
        <Text style={styles.buttonText}>EXPLORE</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f8f9fa",
    justifyContent: "center",
    alignItems: "center",
  },
  card: {
    width: width * 0.8,
    height: height * 0.5,
    borderRadius: 15,
    overflow: "hidden",
    marginRight: 15,
  },
  image: {
    width: "100%",
    height: "100%",
    resizeMode: "cover",
  },
  dotsContainer: {
    flexDirection: "row",
    position: "absolute",
    bottom: 70,
    justifyContent: "center",
  },
  dot: {
    width: 8,
    height: 8,
    borderRadius: 4,
    backgroundColor: "#ccc",
    marginHorizontal: 5,
  },
  activeDot: {
    backgroundColor: "#000",
  },
  exploreButton: {
    position: "absolute",
    bottom: 20,
    backgroundColor: "#000",
    paddingVertical: 10,
    paddingHorizontal: 40,
    borderRadius: 20,
  },
  buttonText: {
    color: "#fff",
    fontSize: 16,
    fontWeight: "bold",
  },
});


export default ShoppingCarousel;
